searchPeople_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilelabel_5': 'searchPeople_mobilelabel_5',
        'mobileradiogroup_27': 'searchPeople_mobileradiogroup_27',
        'radiogroup': 'searchPeople_radiogroup',
        'swimmingRadio': 'searchPeople_swimmingRadio',
        'cyclingRadio': 'searchPeople_cyclingRadio',
        'mobilelabel_23': 'searchPeople_mobilelabel_23',
        'eventDate': 'searchPeople_eventDate',
        'mobilelabel_25': 'searchPeople_mobilelabel_25',
        'addressTextField': 'searchPeople_addressTextField',
        'submitEventSearch': 'searchPeople_submitEventSearch'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'searchPeople';

    /*
     * Nonvisual components
     */
    var datasources = [];

    event_date_service = new Appery.DataSource(ActiveFriend_EventStorage_create_service, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("searchPeople");
        },
        'onSuccess': function(data) {},
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [],
        'requestMapping': [{
            'PATH': ['X-Appery-Database-Id'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{database_id}'
        }, {
            'PATH': ['ARunning'],
            'TYPE': 'STRING',
            'ID': 'radiogroup',
            'ATTR': 'value'
        }, {
            'PATH': ['ASwimming'],
            'TYPE': 'STRING',
            'ID': 'swimmingRadio',
            'ATTR': 'value'
        }, {
            'PATH': ['ACycling'],
            'TYPE': 'STRING',
            'ID': 'cyclingRadio',
            'ATTR': 'value'
        }, {
            'PATH': ['Address'],
            'TYPE': 'STRING',
            'ID': 'addressTextField',
            'ATTR': 'value'
        }, {
            'PATH': ['EventDate'],
            'TYPE': 'STRING',
            'ID': 'eventDate',
            'ATTR': 'defaultDateValue'
        }]
    });

    datasources.push(event_date_service);

    /*
     * Events and handlers
     */

    // Before Show
    searchPeople_beforeshow = function() {
        Appery.CurrentScreen = "searchPeople";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_289E_onLoad = searchPeople_onLoad = function() {
        screen_289E_elementsExtraJS();

        // TODO fire device events only if necessary (with JS logic)
        searchPeople_deviceEvents();
        searchPeople_windowEvents();
        screen_289E_elementsEvents();
    }

    // screen window events
    screen_289E_windowEvents = searchPeople_windowEvents = function() {

        $('#searchPeople').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    }

    // device events
    searchPeople_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

            $(document).bind("resume", function() {
                window.plugins.pushNotification.getPendingNotifications(function(status) {
                    var notifications = status.notifications;
                    for (var i = 0; i < notifications.length; ++i) {
                        window.plugins.pushNotification.notificationCallback(notifications[i]);
                    }
                });
            });
        });
    }

    // screen elements extra js
    screen_289E_elementsExtraJS = searchPeople_elementsExtraJS = function() {
        // screen (searchPeople) extra code

        /* eventDate */

        searchPeople_eventDate_selector = "#searchPeople_eventDate";
        searchPeople_eventDate_dataPickerOptions = {
            dateFormat: "mm/dd/yy",
            firstDay: 0,

            maxDate: new Date(""),

            minDate: new Date(""),

            dayNamesMin: ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'],
            monthNames: ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'],
            showOtherMonths: true
        };

        searchPeople_eventDate_dataPickerOptions.defaultDate = new Date("09/22/2013");

        Appery.__registerComponent('eventDate', new Appery.ApperyMobileDatePickerComponent("searchPeople_eventDate", searchPeople_eventDate_dataPickerOptions));

    }

    // screen elements handler
    screen_289E_elementsEvents = searchPeople_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#searchPeople_mobilecontainer [name="radiogroup"]').die().live({
            change: function() {
                var choose1 = $('input:radio[name=radiogroup]:checked').val();
                var getval = Tiggr('input');
                getval.val(choose1);
            },
        });

        $('#searchPeople_mobilecontainer [name="submitEventSearch"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    try {
                        event_date_service.execute({})
                    } catch (ex) {
                        console.log(ex.name + '  ' + ex.message);
                        hideSpinner();
                    };

                    function twilioSend(name, phone) {
                        $.getJSON("http://siyt.mansilla.com/twilio.php?person=" + name + "&url=http://activeit.demo.com/eid12345&phone=" + phone + "&message=" + name + "%20has%20invited%20you%20to%20a%20fun%20activity%20on%20Active-IT&callback=?", function(data) {

                        });
                    }

                    twilioSend('Neil', '12489742200');
                    twilioSend('Bharath', '16196075327');
                    twilioSend('Vyshakh', '16197619503');
                    Appery.navigateTo('FinalResult', {
                        transition: 'slide',
                        reverse: false
                    });

                }
            },
        });

    }

    $("#searchPeople").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        searchPeople_beforeshow();
    });

    if (runBeforeShow) {
        searchPeople_beforeshow();
    } else {
        searchPeople_onLoad();
    }

}

